import React from 'react';
import './App.css';
import Tabular from './Components/table/tabular';
import styles from './App.module.css'
import CoronaImage from './images/image.png'
import { fetchData } from './api/fetchApi'
import Cards from './Components/Cards/Cards';

class App extends React.Component {

  state = {
    data: {}
  }

  // retrieving data for sending data to summary.jsx
  async componentDidMount() {
    const fetchedData = await fetchData()
    this.setState({ data: fetchedData })
    console.log(fetchedData)
  }

  render() {
    const { data } = this.state;
    return (
      <>
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <a class="navbar-brand" href="#">COVID-19</a>
          <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav mr-auto">
              <li class="nav-item active">
                <a class="nav-link" href="https://covidindiastats.netlify.app/">India</a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="https://neindiacovid19.netlify.app/">NE-India</a>
              </li>

            </ul>

          </div>
        </nav>
        <div className={styles.container}>
          <img className={styles.image} src={CoronaImage} alt='Corona' />
        </div>
        <h4 style={{ textAlign: 'center' }} className="display-6"> WORLD CASES</h4>
        <Cards data={data} />
        {/* <button type="button" class="btn btn-link">Link</button>
        <button type="button" class="btn btn-light">Light</button>
        <button type="button" class="btn btn-dark">Dark</button> */}
        <Tabular />

        <div className="footer-copyright text-center py-3">Â© 2020 Copyright:
    <a href="https://www.linkedin.com/in/khaidemsandip/"> Kurosaki</a>
        </div>
      </>
    );
  }
}

export default App;
