const url = 'https://api.covid19api.com/summary'

export const fetchData = async () => {
    try {
        const { data } = await (await fetch(url)).json()
        const modifiedData = {
            global: data['Global']['NewConfirmed'],
            country: [...data.Countries]
        }
        return modifiedData
    } catch (error) {
        console.log(error)
    }
}
